import Debug from 'debug';
import { config } from 'dotenv';

const debug = Debug('http');

process.on('uncaughtException', (error) => {
  debug('UNCAUGHT EXCEPTION! ðŸ’¥ Shutting down...');
  debug(error.name, error.message);
  process.exit(1);
});

config({ path: './config.env' });
import app from './server/app';

const port = process.env.PORT || 3000;
const server = app.listen(port, () => {
  debug(`App running on port ${port}...`);
});

process.on('unhandledRejection', (error) => {
  debug('UNHANDLED REJECTION! Shutting down...');
  debug(error.name, error.message);
  server.close(() => {
    process.exit(1);
  });
});

export default server;

import express from 'express';
import morgan from 'morgan';
import rateLimit from 'express-rate-limit';
import helmet from 'helmet';
import mongoSanitize from 'express-mongo-sanitize';
import xss from 'xss-clean';
import "@babel/polyfill";

import AppError from './utils/app-error';
import globalError from './utils/global-error';
import Route from './route';

const app = express();

app.use(helmet());
app.use(morgan('dev'));

const limiter = rateLimit({
  windowMs: 60 * 60 * 1000,
  max: 100,
  message: 'Too many requests from this IP, please try again in an hour!',
});
app.use('/api', limiter);
app.use(express.json({ limit: '10kb' }));
app.use(express.urlencoded({ extended: true, limit: '10kb' }));
app.use(mongoSanitize());
app.use(xss());

const router = express.Router();
app.use('/', Route(router));

app.all('*', (req, res, next) => {
  next(new AppError(`Can't find ${req.originalUrl} on this server`, 404));
});

app.use(globalError);

export default app;

export default class AppError extends Error {
  constructor(message, statusCode) {
    super(message);

    this.statusCode = statusCode;
    this.status = `${statusCode}`.startsWith("4") ? "fail" : "error";
    this.isOperational = true;

    Error.captureStackTrace(this, this.constructor);
  }
}

import AppError from './app-error';

const handleCastErrorDB = (error) => {
  const message = `Invalid ${error.path}: ${error.value}`;
  return new AppError(message, 400);
};

const handleDuplicateFieldsDB = (error) => {
  const message = `Duplicate field value: ${error.keyValue.name}. Please use another value!`;
  return new AppError(message, 400);
};

const handleValidationErrorDB = (error) => {
  const errors = Object.values(error.errors).map((element) => element.message);

  const message = `Invalid input data. ${errors.join('. ')}`;
  return new AppError(message, 400);
};

const sendErrorDev = (error, req, res) => {
  if (req.originalUrl.startsWith('/api')) {
    return res.status(error.statusCode).json({
      status: error.status,
      error,
      message: error.message,
      stack: error.stack,
    });
  }
};

const sendErrorProd = (error, req, res) => {
  if (req.originalUrl.startsWith('/api')) {
    // Operational, trusted error: send message to client
    if (error.isOperational) {
      return res.status(error.statusCode).json({
        status: error.status,
        message: error.message,
      });
    }
    // Programming or other unknown error: don't leak this type of error to client but development only
    return res.status(500).json({
      status: 'error',
      message: 'Something went very wrong',
    });
  }
};
export default (error, req, res, next) => {
  error.statusCode = error.statusCode || 500;
  error.status = error.status || 'error';

  if (process.env.NODE_ENV === 'development') {
    sendErrorDev(error, req, res);
  } else if (process.env.NODE_ENV === 'production') {
    let errorObj = { ...error };
    errorObj.message = error.message;

    if (errorObj.kind === 'ObjectId') errorObj = handleCastErrorDB(errorObj);
    if (errorObj.code === 11000) errorObj = handleDuplicateFieldsDB(errorObj);
    if (errorObj._message === 'Validation failed')
      errorObj = handleValidationErrorDB(errorObj);

    sendErrorProd(errorObj, req, res);
  }
};

import userController from '../controllers/userController';
import ruleValidationMiddleware from '../middlewares/ruleValidationMiddleware';
import ruleValidationController from '../controllers/ruleValidationController';

const Route = (router) => {
  router.get('/', userController.getUser);
  router.post(
    '/validate-rule',
    ruleValidationMiddleware.create,
    ruleValidationController.create
  );

  return router;
};

export default Route;

import Response from '../utils/response';
import User from '../../user.json';

export default class userController {
  /**
   * @function getUser
   * @description get User
   *
   * @param {Object} req
   * @param {Object} res
   *
   * @returns {Object} get User JSON Object
   */
  static async getUser(req, res) {
    try {
      return Response.genericResponseMessage(
        'My Rule-Validation API',
        200,
        'success',
        User,
        res
      );
    } catch (error) {
      return Response.genericResponseMessage(
        error.message,
        500,
        'error',
        null,
        res
      );
    }
  }
}

export default class Response {
  static genericResponseMessage(message, statusCode, status, data, res) {
    res.status(statusCode).json({
      message,
      status,
      data,
    });
  }

  static ruleValidationResponseMessage(message, statusCode, status, data, res) {
    res.status(statusCode).json({
      message,
      status,
      data,
    });
  }
}

import Response from '../utils/response';

export default class ruleValidationMiddleware {
  static async create(req, res, next) {
    if (typeof req.body !== 'object') {
      return Response.genericResponseMessage(
        'Invalid JSON payload passed.',
        400,
        'error',
        null,
        res
      );
    }

    const { rule, data } = req.body;
    const { field, condition, condition_value } = rule;

    if (typeof rule === 'object') {
      if (!field) {
        return Response.genericResponseMessage(
          'field is required.',
          400,
          'error',
          null,
          res
        );
      }

      if (!condition) {
        return Response.genericResponseMessage(
          'condition is required.',
          400,
          'error',
          null,
          res
        );
      }

      if (!condition_value) {
        return Response.genericResponseMessage(
          'condition_value is required.',
          400,
          'error',
          null,
          res
        );
      }

      if (typeof field !== 'string') {
        return Response.genericResponseMessage(
          'field should be a string.',
          400,
          'error',
          null,
          res
        );
      }
      if (typeof condition !== 'string') {
        return Response.genericResponseMessage(
          'condition should be a string.',
          400,
          'error',
          null,
          res
        );
      }
      if (typeof condition_value !== 'number') {
        return Response.genericResponseMessage(
          'condition_value should be a number.',
          400,
          'error',
          null,
          res
        );
      }
    } else {
      return Response.genericResponseMessage(
        'rule should be an object.',
        400,
        'error',
        null,
        res
      );
    }

    if (!data) {
      return Response.genericResponseMessage(
        'data is required.',
        400,
        'error',
        null,
        res
      );
    }

    if (typeof data === 'object' || typeof data === 'string') {
      if (typeof data === 'string') {
        let validation = {
          error: true,
          field,
          field_value: condition_value,
          condition,
          condition_value,
        };
        return Response.ruleValidationResponseMessage(
          `field ${field} failed validation.`,
          400,
          'error',
          validation,
          res
        );
      }
    } else {
      return Response.genericResponseMessage(
        'data should be an object, an array or a string.',
        400,
        'error',
        null,
        res
      );
    }

    let splittedField = field.split('.');
    let nestedField;
    if (splittedField) {
      data[splittedField[1]] = data[splittedField[0]][splittedField[1]];
      nestedField = splittedField[1];
      req.body.rule.nestedField = nestedField;

      if (!data[nestedField]) {
        return Response.genericResponseMessage(
          `field ${field} is missing from data.`,
          400,
          'error',
          null,
          res
        );
      }
    } else {
      return Response.genericResponseMessage(
        `field ${field} is missing from data.`,
        400,
        'error',
        null,
        res
      );
    }

    next();
  }
}

import Response from '../utils/response';

export default class ruleValidationController {
  /**
   * @function create
   * @description Create Rule Validation
   *
   * @param {Object} req
   * @param {Object} res
   *
   * @returns {Object} rule validation JSON Object
   */
  static async create(req, res) {
    const { rule, data } = req.body;
    const { field, condition, condition_value, nestedField } = rule;


    let query;

    let validation = {
      error: true,
      field,
      field_value: condition_value,
      condition,
      condition_value,
    };

    switch (condition) {
      case 'eq':
        query = nestedField
          ? data[nestedField] === condition_value
          : data[field] === condition_value;
        break;
      case 'neq':
        query = nestedField
          ? data[nestedField] !== condition_value
          : data[field] !== condition_value;
        break;
      case 'gt':
        query = nestedField
          ? data[nestedField] > condition_value
          : data[field] > condition_value;
        break;
      case 'gte':
        query = nestedField
          ? data[nestedField] >= condition_value
          : data[field] >= condition_value;
        break;
      case 'contains':
        query = nestedField
          ? data[nestedField].includes(condition_value)
          : data[field].includes(condition_value);
        break;

      default:
        return Response.ruleValidationResponseMessage(
          `field ${field} failed validation.`,
          400,
          'error',
          validation,
          res
        );
    }

    validation.error = !query;

    if (query) {
      return Response.ruleValidationResponseMessage(
        `field ${field} successfully validated.`,
        200,
        'success',
        validation,
        res
      );
    } else {
      return Response.ruleValidationResponseMessage(
        `field ${field} failed validation.`,
        400,
        'error',
        validation,
        res
      );
    }
  }
}
